import React, { useEffect, useState } from "react";
import IconSrc from "../imgs/weather-icons/cloudy.png";

const ForcastCard = (props) => {
  const { icon, temp, title, type } = props;
  console.log(props);
  const [cardTitle, setCardTitle] = useState("");

  const unixToDay = (unix_timestamp) => {
      console.log(new Date(unix_timestamp).getDay());
    return (new Date(unix_timestamp).getDay());

  };

  const unixToTime = (unix_timestamp) => {
    let date = new Date(unix_timestamp * 1000);
    let hours = date.getHours();
    return hours;
  };
  useEffect(() => {
    if (type == "daily") {
        let date = unixToDay(title);
        setCardTitle(date);
    } else if (type == "hourly") {
        let time = unixToTime(title);
        setCardTitle(time);
    }
  }, []);

  return (
    <div>
      <h3>{cardTitle}</h3>
      <div>
        <img src={IconSrc} />
      </div>
      {type=="hourly" ?
      <span>{temp}</span>
      :
      <span>{temp[0]} /{temp[1]}</span>
  }
    </div>
  );
};
export default ForcastCard;
