import React, { useEffect, useState } from "react";
import Header from "components/Header";
import MainBanner from "components/MainBanner";
import Forcast from "components/Forcast";
import MainBG from "../imgs/Background.png";

const Container = () => {
  const [geolocationLoaded, setGeolocationLoaded] = useState(false);
  const [weatherLoaded, setWeatherLoaded] = useState(false);
  const [userCoordinates, setUserCoordinates] = useState({
    lat: 0,
    long: 0,
  });
  const [currentCity, setCurrentCity] = useState("");
  const [currentDate, setCurrentDate] = useState("");
  const [currentTempType,setCurrentTempType]=useState('f');
  const [currentForcast , setCurrentForcast] = useState(null);
  const [hourlyForcast,setHourlyForcast] = useState(null);
  const [DailyForcast, setDailyForcast] = useState(null);
  const APP_URL = "http://localhost:5000/weather/";

  const unixToDateTime = (unix_timestamp) => {

    var options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    const dateVal = new Date(unix_timestamp).toLocaleDateString('en-US',options);
    console.log(new Date(unix_timestamp));
    console.log(dateVal);
    return dateVal;
  };

  function convertDateToUTC(date) { 
    return new Date(date.getUTCFullYear(), date.getUTCMonth(), date.getUTCDate(), date.getUTCHours(), date.getUTCMinutes(), date.getUTCSeconds()); 
    }
  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const newCoordinates = {
            lat: position.coords.latitude,
            long: position.coords.longitude,
          };
          setUserCoordinates(newCoordinates);
        },
        (error) => {
          const newCoordinates = {
            lat: -1,
            long: -1,
          };
          setUserCoordinates(newCoordinates);
        }
      );
    } else {
      const newCoordinates = {
        lat: -1,
        long: -1,
      };
      setUserCoordinates(newCoordinates);
    }
  };

  useEffect(() => {
    getLocation();
  }, []);

  useEffect(() => {
    fetch(
      `https://nominatim.openstreetmap.org/reverse?format=json&lat=${userCoordinates.lat}&lon=${userCoordinates.long}&zoom=18&addressdetails=1`
    )
      .then((response) => response.json())
      .then((location) => {
        console.log(location.display_name);
        setCurrentCity(location.display_name);
       setGeolocationLoaded(true);
      });

      if (userCoordinates.lat !== 0 && userCoordinates.long !== 0) {
        fetch(
          APP_URL + `?lat=${userCoordinates.lat}&long=${userCoordinates.long}`
        )
          .then((response) => response.json())
          .then((forcast) => {
            let formattedDate = unixToDateTime(forcast.currently.time);
            setCurrentDate(formattedDate);
            setCurrentForcast(forcast);
            setDailyForcast(forcast.daily);
            setHourlyForcast(forcast.hourly)
            setWeatherLoaded(true);
          });

      }

  }, [userCoordinates]);



  return (
    <div className="container">
      <Header className="header" />
      <div className="header-bg">
        <img className="header-bgImg" src={MainBG} />
      </div>
      {(weatherLoaded && geolocationLoaded) && (
        <>
          <MainBanner city={currentCity} date={currentDate} forcast={currentForcast}/>
          <Forcast hourly={hourlyForcast} daily={DailyForcast}/>
        </>
      )}
    </div>
  );
};
export default Container;
