import React, { useEffect, useState } from "react";
import IconSrc from "../imgs/weather-icons/cloudy.svg";

const ForcastCard = (props) => {
  const { icon, temp, title, type } = props;
  console.log(props);
  const [cardTitle, setCardTitle] = useState("");

  const unixToDay = (unix_timestamp) => {
    let i = 0;
    let data = { list: [ { timeStamp: unix_timestamp } ] };
    let days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']; 
    let dayNum = new Date(data.list[i].timeStamp * 1000).getDay();
    let dayName = days[dayNum];
    return dayName;

  };

  const ReplaceImg=(icon) =>
{
    var the_arr = IconSrc.split('/');
    the_arr.pop();
    console.log(the_arr);
    return( the_arr.join('/')+ icon +'.svg' );
}

  const unixToTime = (unix_timestamp) => {
    let date = new Date(unix_timestamp * 1000);
    let hours = (date.getHours()) < 10 ? "0"+date.getHours()  : date.getHours() ;
    let formattedHour = hours+":00";
    return formattedHour;
  };
  useEffect(() => {
    if (type == "daily") {
        let date = unixToDay(title);
        setCardTitle(date);
    }  if (type == "hourly") {
        let time = unixToTime(title);
        setCardTitle(time);
    }
  }, []);

  return (
    <div>
      <h3>{cardTitle}</h3>
      <div>

        <img src={ReplaceImg(icon)} />
      </div>
      {type=="hourly" ?
      <span>{temp}</span>
      :
      <span>{temp[0]} /{temp[1]}</span>
  }
    </div>
  );
};
export default ForcastCard;
