import React, { useEffect, useState } from "react";
import Header from "components/Header";
import MainBanner from "components/MainBanner";
import Forcast from "components/Forcast";
import MainBG from "../imgs/Background.png";
import Skeleton, { SkeletonTheme } from "react-loading-skeleton";

const Container = () => {
  const [loading, setLoading] = useState(false);
  const [userCoordinates, setUserCoordinates] = useState({
    lat: null,
    long: null,
  });
  const [userCity, setUserCity] = useState("");
  const APP_URL = "http://localhost:5000/weather/";

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const newCoordinates = {
            lat: position.coords.latitude,
            long: position.coords.longitude,
          };
          setUserCoordinates(newCoordinates);
        },
        (error) => {
          const newCoordinates = {
            lat: -1,
            long: -1,
          };
          setUserCoordinates(newCoordinates);
        }
      );
    } else {
      const newCoordinates = {
        lat: -1,
        long: -1,
      };
      setUserCoordinates(newCoordinates);
    }
  };

  useEffect(() => {
    getLocation();
  }, []);

  useEffect(() => {
    fetch(
      `https://nominatim.openstreetmap.org/reverse?format=json&lat=${userCoordinates.lat}&lon=${userCoordinates.long}&zoom=18&addressdetails=1`
    )
      .then((response) => response.json())
      .then((location) => {
        setUserCity(location.display_name)
      });
  }, [userCoordinates]);

  useEffect(() => {
    fetch(APP_URL + `?lat=${userCoordinates.lat}&long=${userCoordinates.long}`)
      .then((response) => response.json())
      .then((forcast) => {
        console.log(forcast);
      });
      setLoading(true);
  }, [userCoordinates]);

  return (
    <div className="container">
      <Header className="header" />
      <div className="header-bg">
        <img className="header-bgImg" src={MainBG} />
      </div>
      {loading ?  
      <>
       <MainBanner />
       <Forcast />  
       </>
       :
       <SkeletonTheme color="#202020" highlightColor="#444">
       <p>
         <Skeleton count={3} />
       </p>
     </SkeletonTheme>
    }

    </div>
  );
};
export default Container;
