import React from "react";

const MainBanner = (props) => {
    console.log(props);
    const images = require.context('../../public/weather-icons', true);

    const {city,date,forcast}= props;
    return (
        <div className="mainBanner">
            <div className="mainBanner-cont--left">
                <h1>{city}</h1>
                <h5>{date}</h5>
                <div>
                <img src={`${process.env.PUBLIC_URL}/weather-icons/${forcast.currently.icon}.svg`}  />

                </div>
                <h3>{forcast.currently.summary}</h3>
            </div>
            <div className="mainBanner-cont--right">
                <h2> {forcast.currently.temperature}째 </h2>
                <p>{forcast.daily.data[0].temperatureHigh}째 / {forcast.daily.data[0].temperatureLow}째</p>
                <p>{forcast.daily.data[0].summary}째</p>
            </div>
        </div>
    );
};
export default MainBanner;
