import React,{useEffect,useState} from "react";
import Header from "components/Header";
import MainBanner from "components/MainBanner";
import Forcast from "components/Forcast";
import MainBG from "../imgs/Background.png";
import Geocode from "react-geocode";

const Container = () => {

    const [loading, setLoading] = useState(false);
    const [latitude,setLatitude]=useState(null);
    const [longitude,setLongitude]=useState(null);

    const APP_URL = 'http://localhost:5000/weather/';
    Geocode.setApiKey("AIzaSyANbc8ynnbm7NkHY0qUYSjnfzMfjxyh8Tg");
    Geocode.setLanguage("en");



  const getLocation=()=>{
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {

              setLatitude(position.coords.latitude);
              setLongitude(position.coords.longitude);

          },
          (error) => {
            setLatitude(-1);
            setLongitude(-1);
          }
        );
      } else {
        setLatitude(-1);
        setLongitude(-1);
      }
    }


    useEffect(()=>{
      getLocation();
    },[])

    useEffect(()=>{
      console.log(latitude,longitude);
      Geocode.fromLatLng(latitude, longitude).then(
        (response) => {
          const address = response.results[0].formatted_address;
          let city, country;
          for (let i = 0; i < response.results[0].address_components.length; i++) {
            for (let j = 0; j < response.results[0].address_components[i].types.length; j++) {
              switch (response.results[0].address_components[i].types[j]) {
                case "locality":
                  city = response.results[0].address_components[i].long_name;
                  break;

                case "country":
                  country = response.results[0].address_components[i].long_name;
                  break;
              }
            }
          }
          console.log(city, country);
          console.log(address);
        },
        (error) => {
          console.error(error);
        }
      );

    },[longitude,latitude])

    useEffect(()=>{

     fetch(APP_URL+`?lat=${latitude}&long=${longitude}`)
     .then(response=>response.json())
     .then(forcast=>{
       console.log(forcast)
     })
    },[longitude,latitude])

    return (
        <div className="container">
            <Header className="header"/>
            <div className="header-bg">
                <img className="header-bgImg" src={MainBG} />
            </div>
            <MainBanner />
            <Forcast />
        </div>
    );
};
export default Container;
