import React, { useState } from "react";
import DailyForcast from "components/DailyForcast";
import HourlyForcast from "components/HourlyForcast";

const Forcast = (props) => {
    const {daily,hourly,tempType} = props;
    const [activeType,setActiveType]=useState("hourly");
    return (
        <div>
            <div>
                <span className={`${activeType == "hourly" && "active"} type-item`}>Hourly</span>
                <span className={`${activeType == "daily" && "active"} type-item`}>Daily</span>
            </div>
            <div>
                <HourlyForcast hourly={hourly}  />
                <DailyForcast daily={daily}/>
            </div>

        </div>
    );
};
export default Forcast;
