import React, { useEffect, useState } from "react";
import Header from "components/Header";
import MainBanner from "components/MainBanner";
import Forcast from "components/Forcast";
import MainBG from "../imgs/Background.png";

const Container = () => {
  const [loading, setLoading] = useState(false);
  const [userCoordinates, setUserCoordinates] = useState(null);

  const APP_URL = "http://localhost:5000/weather/";

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const newCoordinates = {
                'lat':position.coords.latitude,
                'long':position.coords.longitude
          }
          setUserCoordinates(newCoordinates);
        },
        (error) => {
          const newCoordinates = {
            'lat':-1,
            'long':-1
      }
      setUserCoordinates(newCoordinates);
        }
      );
    } else {
      const newCoordinates = {
        'lat':-1,
        'long':-1
  }
  setUserCoordinates(newCoordinates);
    }
  };

  useEffect(() => {
    getLocation();
  }, []);

  useEffect(() => {
    fetch(
      `https://nominatim.openstreetmap.org/reverse?format=json&lat=${userCoordinates.latitude}&lon=${userCoordinates.longitude}&zoom=18&addressdetails=1`
    )
      .then((response) => response.json())
      .then((location) => {
        console.log(location);
      });
  }, [userCoordinates]);

  useEffect(() => {
    fetch(APP_URL + `?lat=${userCoordinates.latitude}&long=${userCoordinates.longitude}`)
      .then((response) => response.json())
      .then((forcast) => {
        console.log(forcast);
      });
  }, [userCoordinates]);

  return (
    <div className="container">
      <Header className="header" />
      <div className="header-bg">
        <img className="header-bgImg" src={MainBG} />
      </div>
      <MainBanner />
      <Forcast />
    </div>
  );
};
export default Container;
