import React,{useEffect,useState} from "react";
import Header from "components/Header";
import MainBanner from "components/MainBanner";
import Forcast from "components/Forcast";
import MainBG from "../imgs/Background.png";
import LocationIQ from 'react-native-locationiq';

const Container = () => {

    const [loading, setLoading] = useState(false);
    const [latitude,setLatitude]=useState(null);
    const [longitude,setLongitude]=useState(null);

    const APP_URL = 'http://localhost:5000/weather/';

    LocationIQ.init("71922993163d66a54356d6aca17ccdfe"); // use a valid API key

  const getLocation=()=>{
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {

              setLatitude(position.coords.latitude);
              setLongitude(position.coords.longitude);

          },
          (error) => {
            setLatitude(-1);
            setLongitude(-1);
          }
        );
      } else {
        setLatitude(-1);
        setLongitude(-1);
      }
    }


    useEffect(()=>{
      getLocation();
    },[])

    useEffect(()=>{
      LocationIQ.reverse(latitude, longitude)
      .then(json => {
          var address = json.address;
          console.log(address);
      })
      .catch(error => console.warn(error));
    },[longitude,latitude])

    useEffect(()=>{

     fetch(APP_URL+`?lat=${latitude}&long=${longitude}`)
     .then(response=>response.json())
     .then(forcast=>{
       console.log(forcast)
     })
    },[longitude,latitude])

    return (
        <div className="container">
            <Header className="header"/>
            <div className="header-bg">
                <img className="header-bgImg" src={MainBG} />
            </div>
            <MainBanner />
            <Forcast />
        </div>
    );
};
export default Container;
