import React, { useEffect, useState } from "react";
import Header from "components/Header";
import MainBanner from "components/MainBanner";
import Forcast from "components/Forcast";
import MainBG from "../imgs/Background.png";

const Container = () => {
  const [loading, setLoading] = useState(false);
  const [userCoordinates, setUserCoordinates] = useState({
    lat: 0,
    long: 0,
  });
  const [currentCity, setCurrentCity] = useState("");
  const [currentDate,setCurrentDate]=useState("");
  const APP_URL = "http://localhost:5000/weather/";

  const unixToDateTime = (unix_timestamp) => {
    // Create a new JavaScript Date object based on the timestamp
    // multiplied by 1000 so that the argument is in milliseconds, not seconds.
    var date = new Date(unix_timestamp * 1000);
    // Hours part from the timestamp
    var hours = date.getHours();
    // Minutes part from the timestamp
    var minutes = "0" + date.getMinutes();
    // Seconds part from the timestamp
    var seconds = "0" + date.getSeconds();

    // Will display time in 10:30:23 format
    var formattedTime =
      hours + ":" + minutes.substr(-2) + ":" + seconds.substr(-2);
      return(formattedTime);
  };

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const newCoordinates = {
            lat: position.coords.latitude,
            long: position.coords.longitude,
          };
          setUserCoordinates(newCoordinates);
        },
        (error) => {
          const newCoordinates = {
            lat: -1,
            long: -1,
          };
          setUserCoordinates(newCoordinates);
        }
      );
    } else {
      const newCoordinates = {
        lat: -1,
        long: -1,
      };
      setUserCoordinates(newCoordinates);
    }
  };

  useEffect(() => {
    getLocation();
  }, []);

  useEffect(() => {


    fetch(
      `https://nominatim.openstreetmap.org/reverse?format=json&lat=${userCoordinates.lat}&lon=${userCoordinates.long}&zoom=18&addressdetails=1`
    )
      .then((response) => response.json())
      .then((location) => {
        setCurrentCity(location.display_name);
      });



  }, [userCoordinates]);

  useEffect(() => {
    if(userCoordinates.lat !== 0 && userCoordinates.long !== 0){
    fetch(APP_URL + `?lat=${userCoordinates.lat}&long=${userCoordinates.long}`)
      .then((response) => response.json())
      .then((forcast) => {
        console.log(forcast.currently)
        setLoading(true);

        console.log(forcast);
      });
    }
  }, [userCoordinates]);

  return (
    <div className="container">
      <Header className="header" />
      <div className="header-bg">
        <img className="header-bgImg" src={MainBG} />
      </div>
{loading &&
<>
      <MainBanner city={currentCity}  date={currentDate} />
      <Forcast />
      </>
}
    </div>
  );
};
export default Container;
